import { useEffect, useState } from "react";
import { Trash2, Plus, Edit3, Sun, Moon } from "lucide-react";

export default function App() {
  const [todos, setTodos] = useState(() => {
    const saved = localStorage.getItem("todos");
    return saved ? JSON.parse(saved) : [];
  });
  const [text, setText] = useState("");
  const [editId, setEditId] = useState(null);
  const [filter, setFilter] = useState("all");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = () => {
    if (text.trim()) {
      if (editId) {
        setTodos(todos.map(todo => todo.id === editId ? { ...todo, text } : todo));
        setEditId(null);
      } else {
        setTodos([...todos, { id: Date.now(), text, done: false }]);
      }
      setText("");
    }
  };

  const toggleDone = (id) => {
    setTodos(todos.map(todo => todo.id === id ? { ...todo, done: !todo.done } : todo));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === "active") return !todo.done;
    if (filter === "done") return todo.done;
    return true;
  });

  return (
    <div className={`${darkMode ? "bg-gray-900 text-white" : "bg-pink-100 text-gray-800"} min-h-screen flex items-center justify-center p-4`}>
      <div className="w-full max-w-md bg-white dark:bg-gray-800 shadow-lg rounded-3xl p-6 border-2 border-pink-300 dark:border-gray-600">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl font-bold tracking-wide text-pink-600 dark:text-white">To Do</h1>
          <button onClick={() => setDarkMode(!darkMode)} className="text-pink-500 dark:text-yellow-300">
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>

        <div className="flex gap-2 mb-4">
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="flex-grow p-2 rounded-xl border border-pink-300 dark:border-gray-600 focus:outline-none"
            placeholder="Write a task..."
          />
          <button
            onClick={addTodo}
            className="bg-pink-400 hover:bg-pink-500 text-white p-2 rounded-xl"
          >
            <Plus size={18} />
          </button>
        </div>

        <div className="flex justify-between mb-3 text-sm">
          {["all", "active", "done"].map(f => (
            <button
              key={f}
              onClick={() => setFilter(f)}
              className={`px-2 py-1 rounded-full ${filter === f ? "bg-pink-300 text-white" : "text-pink-500"}`}
            >
              {f.charAt(0).toUpperCase() + f.slice(1)}
            </button>
          ))}
        </div>

        <ul className="space-y-3">
          {filteredTodos.map((todo) => (
            <li key={todo.id} className="flex justify-between items-center bg-pink-50 dark:bg-gray-700 border border-pink-200 dark:border-gray-600 rounded-xl p-3">
              <span
                onClick={() => toggleDone(todo.id)}
                className={`${todo.done ? "line-through text-gray-400" : ""} cursor-pointer flex-grow`}
              >
                {todo.text}
              </span>
              <div className="flex gap-2">
                <button onClick={() => { setEditId(todo.id); setText(todo.text); }} className="text-pink-400 hover:text-pink-600">
                  <Edit3 size={18} />
                </button>
                <button onClick={() => deleteTodo(todo.id)} className="text-pink-400 hover:text-pink-600">
                  <Trash2 size={18} />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
