import { useEffect, useState } from "react";
import { Trash2, Plus, Edit3, CheckCircle2 } from "lucide-react";

export default function App() {
  const [todos, setTodos] = useState(() => {
    const saved = localStorage.getItem("todos");
    return saved ? JSON.parse(saved) : [];
  });
  const [text, setText] = useState("");
  const [editId, setEditId] = useState(null);
  const [filter, setFilter] = useState("all");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = () => {
    if (text.trim()) {
      if (editId) {
        setTodos(todos.map(todo => todo.id === editId ? { ...todo, text } : todo));
        setEditId(null);
      } else {
        setTodos([...todos, { id: Date.now(), text, done: false }]);
      }
      setText("");
    }
  };

  const toggleDone = (id) => {
    setTodos(todos.map(todo => todo.id === id ? { ...todo, done: !todo.done } : todo));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const filteredTodos = todos
    .filter(todo => {
      if (filter === "active") return !todo.done;
      if (filter === "done") return todo.done;
      return true;
    })
    .sort((a, b) => a.done - b.done); // done = false comes first

  return (
    <div className={`${darkMode ? "bg-[#2b2d42] text-white" : "bg-pink-100 text-gray-800"} min-h-screen flex items-center justify-center p-4`}>
      <div className="w-full max-w-md bg-white dark:bg-[#3b3f58] shadow-lg rounded-3xl p-6 border-[3px] border-dashed border-pink-300 dark:border-pink-500">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl font-bold tracking-wide text-pink-600 dark:text-pink-200">To Do</h1>
          <label className="inline-flex items-center cursor-pointer">
            <input type="checkbox" className="sr-only peer" checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
            <div className="w-11 h-6 bg-pink-300 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-0.5 after:left-[4px] after:bg-white after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-yellow-400 relative"></div>
          </label>
        </div>

        <div className="flex gap-2 mb-4">
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="flex-grow p-2 rounded-xl border border-pink-300 dark:border-pink-500 focus:outline-none"
            placeholder="Write a task..."
          />
          <button
            onClick={addTodo}
            className="bg-pink-400 hover:bg-pink-500 text-white p-2 rounded-xl"
          >
            <Plus size={18} />
          </button>
        </div>

        <div className="flex justify-between mb-3 text-sm">
          {[
            ["all", "All"],
            ["active", "Active"],
            ["done", "Done"]
          ].map(([key, label]) => (
            <button
              key={key}
              onClick={() => setFilter(key)}
              className={`px-3 py-1 rounded-full font-medium ${
                filter === key
                  ? "bg-pink-300 text-white dark:bg-pink-500"
                  : "text-pink-500 dark:text-pink-300"
              }`}
            >
              {label}
            </button>
          ))}
        </div>

        <ul className="space-y-3">
          {filteredTodos.map((todo) => (
            <li
              key={todo.id}
              className={`flex justify-between items-center border rounded-xl p-3 transition-all ${
                todo.done
                  ? "bg-green-100 dark:bg-green-700 border-green-300 dark:border-green-500"
                  : "bg-pink-50 dark:bg-[#4c516e] border-pink-200 dark:border-pink-500"
              }`}
            >
              <div
                onClick={() => toggleDone(todo.id)}
                className="flex items-center gap-2 cursor-pointer flex-grow"
              >
                {todo.done && <CheckCircle2 className="text-green-500" size={18} />}
                <span className={`font-medium ${todo.done ? "line-through text-gray-400 dark:text-gray-300" : "dark:text-white"}`}>
                  {todo.text}
                </span>
              </div>
              <div className="flex gap-2">
                <button
                  onClick={() => {
                    setEditId(todo.id);
                    setText(todo.text);
                  }}
                  className="text-pink-400 hover:text-pink-600 dark:hover:text-white"
                >
                  <Edit3 size={18} />
                </button>
                <button
                  onClick={() => deleteTodo(todo.id)}
                  className="text-pink-400 hover:text-pink-600 dark:hover:text-white"
                >
                  <Trash2 size={18} />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
